import { useState } from "react"

function Note({ id, date, content, important, editNote, deleteNote }) {
  const [note, setNote] = useState(content)
  const [editing, setEditing] = useState(false)
  const [tooltip, setTooltip] = useState(false)

  const updateNote = () => {
    editNote(note, important, id)
    setEditing(false)
  }
  const toggleImportant = () => {
    editNote(note, !important, id)
  }

  return (
    <div className="note relative" onMouseEnter={() => setTooltip(true)} onMouseLeave={() => setTooltip(false)}>
      {(
        tooltip && <span className="delete hover:cursor-pointer absolute z-50 rounded-full bg-white text-black hover:bg-red-400 text-center flex justify-center items-center" style={{right: '-5px', top: '-10px', width: '32px', height: '32px'}} onClick={() => deleteNote(id)}>üóëÔ∏è</span>
      )}

      {(
        tooltip && <span id="edit_button" className="hover:cursor-pointer absolute z-50 rounded-full bg-white text-black hover:bg-green-500 text-center flex justify-center items-center" style={{left: '-5px', top: '-10px', width: '32px', height: '32px'}} onClick={() => setEditing(!editing)}>‚úèÔ∏è</span>
      )}

      {(
        tooltip && <span id="pin_button" className="hover:cursor-pointer absolute z-50 rounded-full bg-white text-black hover:bg-green-500 text-center flex justify-center items-center" style={{left: '30px', top: '-10px', width: '32px', height: '32px', opacity: important ? '1' : '0.25'}} onClick={toggleImportant}>üìå</span>

      )}

      <div className={`${important ? 'bg-cyan-600' : 'bg-slate-500'} aspect-h-1 aspect-w-1 w-68 h-32 overflow-hidden rounded-md  lg:aspect-none group-hover:opacity-75 p-5`}>
        {date}
        <br></br>
        {(editing ? <input className="edit-input text-black font-bold border-0 outline-none" value={note} onChange={(e) => setNote(e.target.value)}/> : <strong>{content}</strong> )}
        {(editing && <div className="text-xl hover:cursor-pointer"><span onClick={() => setEditing(false)}>‚úñÔ∏è</span> <span onClick={() => updateNote()}>‚úîÔ∏è</span></div>)}

      </div>

    </div>
  )
}

export default Note

import { useState, useEffect } from 'react'

function SignUpForm({ username, password, confirmPassword, setUsername, setPassword, setConfirmPassword, authenticate, type }) {
  const [passwordMatchError, setPasswordMatchError] = useState('')

  useEffect(() => {
    if (password !== confirmPassword) {
      setPasswordMatchError('Passwords do not match')
    } else {
      setPasswordMatchError('')
    }
  }, [password, confirmPassword])

  return (
    <div className="flex flex-col items-center">
      <input
        className="p-4 rounded-lg"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />

      <input
        className="p-4 mt-5 rounded-lg"
        placeholder="Password"
        type="password"
        value={password}
        onChange={(e) => {
          setPassword(e.target.value)
        }}
      />

      <input
        className="p-4 mt-5 rounded-lg"
        placeholder="Confirm password"
        type="password"
        value={confirmPassword}
        onChange={(e) => {
          setConfirmPassword(e.target.value)
        }}
      />

      {passwordMatchError && <p className="text-red-500 mt-3">{passwordMatchError}</p>}

      <button
        className="text-white mt-5"
        onClick={() => {
          if (!passwordMatchError) {
            authenticate()
          }
        }}>
        {type}
      </button>
    </div>
  )
}

export default SignUpForm

import '../index.css';
import logo from '../assets/logo.png';
import { useNavigate, Link } from 'react-router-dom';
import LoginForm from '../components/LoginForm';
import { useState } from 'react';
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const authenticate = () => {
    const credentials = {
      username: username,
      password: password,
    };

    axios.post('http://localhost:8420/v1/login', credentials).then((resp) => {
      console.log(resp);
      if (resp.status == 200) {
        localStorage.setItem('token', resp.data.token);
        navigate('/home');
      }
    });
  };

  return (
    <div className='flex items-center flex-col'>
      <img className='size-1/2 object-contain' src={logo} />

      <LoginForm
        username={username}
        password={password}
        setPassword={setPassword}
        setUsername={setUsername}
        authenticate={authenticate}
        type='Login'
      />

      <p className='mt-3'>
        Don't have an account yet?
        <strong>
          <Link to='/signup'> Sign up here</Link>
        </strong>
        .
      </p>
    </div>
  );
}

export default Login;

